#!/usr/bin/env python
import os
import jinja2
import webapp2
import random

class City(object): # ustvarimo razred mesto
    def __init__(self, name, country, imgUrl):
        self.name = name,
        self.country = country,
        self.imgUrl = imgUrl

# naredimo tri instance tega razreda
ljubljana = City(name="Ljubljana", country="Slovenia", imgUrl="https://s.inyourpocket.com/gallery/121232.jpg")
prague = City(name="Prague", country="Czech Republic", imgUrl ="data:image/jpeg;base64,")
paris = City(name="Paris", country="France", imgUrl="http://multi.speedybooker.com/pool/paris-1.jpg")

listOfCities = [ljubljana, prague, paris] # jih shranimo v seznam
secretCountry = "no value" # zaenkrat ni nobena od drzav se izbrana

template_dir = os.path.join(os.path.dirname(__file__), "templates")
jinja_env = jinja2.Environment(loader=jinja2.FileSystemLoader(template_dir), autoescape=False)


class BaseHandler(webapp2.RequestHandler):

    def write(self, *a, **kw):
        return self.response.out.write(*a, **kw)

    def render_str(self, template, **params):
        t = jinja_env.get_template(template)
        return t.render(params)

    def render(self, template, **kw):
        return self.write(self.render_str(template, **kw))

    def render_template(self, view_filename, params=None):
        if params is None:
            params = {}
        template = jinja_env.get_template(view_filename)
        return self.response.out.write(template.render(params))


#handlerji do posameznih spletnih strani

class MainHandler(BaseHandler):
    def get(self):
        return self.render_template("index.html")

class AboutHandler(BaseHandler):
    def get(self):
        return self.render_template("about.html")

class ProjectsHandler(BaseHandler):
	def get(self):
		return self.render_template("projects.html")

class BlogHandler(BaseHandler):
	def get(self):
		return self.render_template("blog.html")

class ContactHandler(BaseHandler):
	def get(self):
		return self.render_template("contact.html")

class CheckMessageHandler(BaseHandler):
	def post(self):
		name = self.request.get("name")
		email = self.request.get("mail")
		message = self.request.get("message")
		self.write(name)
		self.write(email)
		self.write(message)


class guessCountryHandler(BaseHandler):
    def get(self):

        randomChoice = random.randint(0,2) # generiraj nakljucno stevilo od 0 do vkljucno z 2
        selected = listOfCities[randomChoice] # nato poisci v seznamu mest tisto mesto, ki ima index nakljucnega stevila

        nameOfCity = selected.name # izlusci ime mesta
        countryOfCity = selected.country # izlusci drzavo mesta
        cityImg = selected.imgUrl # izlusci sliko mesta

        global secretCountry # globalna spremenljivka, ki doloca nakljucno izbrano mesto
        secretCountry = countryOfCity # naj postane le to mesto

        params = {"name": nameOfCity, "country" : countryOfCity, "img" : cityImg} # vse vrednosti shranimo v parametre..

        return self.render_template("guessCountry.html",params) # in jih podamo ob prikazu spletne strani


class checkAnswerHandler(BaseHandler):

    def post(self):
        answer = str(self.request.get("list_of_countries")) # po post metodi dobi izbrano vrednost
        img = "" # ta spremenljivka je zaenkrat prazna

        result = "" # tudi prazna
        if str(secretCountry) == str(answer): # v kolikor je vrednost izbranega odgovora enaka vrednosti skritega mesta...
            result = "Congratulations! Your answer was correct!" # izpisi ta del kode
            img = "right"  # doloci to vrednost za "img" spremenljivko, le ta v html doloci katera slika se bo prikazala med rezultati
        else:
            result = "Sorry, your answer was incorrect!" # sicer izvedi ta del kode
            img ="wrong" # ter doloci to vrednost spremenljivke

        params = {"result" : result, "img": img} # vse shranimo v parametre

        return self.render_template("guessCountryResult.html", params) # in jih podamo ob prikazu spletne strani

app = webapp2.WSGIApplication([
    webapp2.Route('/', MainHandler),
    webapp2.Route("/about",AboutHandler),
    webapp2.Route("/projects", ProjectsHandler),
    webapp2.Route("/blog",BlogHandler),
    webapp2.Route("/contact",ContactHandler),
    webapp2.Route("/checkMessage", CheckMessageHandler),
    webapp2.Route("/guessCountry", guessCountryHandler), # pot do kviza z glavnimi mesti
    webapp2.Route("/checkAnswer", checkAnswerHandler),  # pot za preverjanje odgovora
], debug=True)
